import { useState, useContext } from "react";
import axios from "axios";
import { UserContext } from "./UserContext";
import { useNavigate, useLocation } from "react-router-dom";
import useAuth from "./hooks/useAuth";

/*
  function getJwtTokenFromCookie() {
    // Implement logic to get the JWT token from the cookie
    // Example logic (this may vary depending on your application):
    const cookieName = "jwt";
    const cookies = document.cookie.split(";");
    for (let cookie of cookies) {
      const [name, value] = cookie.split("=");
      if (name.trim() === cookieName) {
        return value;
      }
    }
    return ""; // Return empty string if token not found or handle accordingly
  }

await axios
.get("http://localhost:8080/getAllAccounts", {
  withCredentials: true,
  headers: {
    Authorization: `Bearer ${getJwtTokenFromCookie()}`, // Include JWT token from cookie
  },
})
.then((response) => {
  console.log(response.data); // Handle successful response here
})
.catch((error) => {
  console.error("Error fetching accounts:", error); // Handle error case here
}); */

function Login() {
  /*const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);
  const { dispatch } = useContext(UserContext);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.post(
        "http://localhost:8080/login",
        { username, password },
        { withCredentials: true }
      );

      // Dispatch an action to set the user data in the context
      dispatch({ type: "SET_USER", payload: { username } });

      setError(null);

      navigate("/usermanagement"); // Navigate to usermanagement
    } catch (error) {
      if (error.response) {
        console.error("Error response:", error.response.data);
        setError(error.response.data.message);
      } else if (error.request) {
        console.error("No response received:", error.request);
        setError("Network error. Please try again later.");
      } else {
        console.error("Request setup error:", error.message);
        setError("An unexpected error occurred. Please try again.");
      }
    }
  }; */

  const navigate = useNavigate();
  const location = useLocation();
  const from = location.state?.from?.pathname || "/linkpage";
  const { auth, setAuth } = useAuth();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios
        .post(
          "http://localhost:8080/login",
          { username, password },
          { withCredentials: true }
        )
        .then((res) => {
          if (res?.data.name) {
            const role = res?.data.role;
            setAuth({ role: `${role}`, username: `${name}` });
            setName("");
            setPassword("");
            navigate(from, { replace: true });
          } else {
            console.log("incorrect submission");
            setError(res.message);
          }
        });
    } catch (err) {
      if (!err?.response) {
        setError("no server response");
      } else {
        setError("registeration failed");
      }
    }
  };

  return (
    <>
      <div className="flex min-h-full flex-1 flex-col justify-center px-6 py-12 lg:px-8">
        <div className="sm:mx-auto sm:w-full sm:max-w-sm">
          <img
            alt="Your Company"
            src="https://tailwindui.com/img/logos/mark.svg?color=indigo&shade=600"
            className="mx-auto h-10 w-auto"
          />
          <h2 className="mt-10 text-center text-2xl font-bold leading-9 tracking-tight text-gray-900">
            Sign in to your account
          </h2>
        </div>

        <div className="mt-10 sm:mx-auto sm:w-full sm:max-w-sm">
          <form onSubmit={handleSubmit} className="space-y-6">
            <div>
              <label
                htmlFor="username"
                className="block text-sm font-medium leading-6 text-gray-900"
              >
                Username
              </label>

              <div className="mt-2">
                <input
                  id="username"
                  name="username"
                  type="text"
                  required
                  autoComplete="username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
                />
              </div>
            </div>

            <div>
              <div className="flex items-center justify-between">
                <label
                  htmlFor="password"
                  className="block text-sm font-medium leading-6 text-gray-900"
                >
                  Password
                </label>

                <div className="text-sm">
                  <a
                    href="#"
                    className="font-semibold text-indigo-600 hover:text-indigo-500"
                  >
                    Forgot password?
                  </a>
                </div>
              </div>
              <div className="mt-2">
                <input
                  id="password"
                  name="password"
                  type="password"
                  required
                  autoComplete="current-password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
                />
              </div>
            </div>

            <div>
              <button
                type="submit"
                className="flex w-full justify-center rounded-md bg-indigo-600 px-3 py-1.5 text-sm font-semibold leading-6 text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600"
              >
                Sign in
              </button>
            </div>
            {error && <p className="text-red-500 mt-2">{error}</p>}
          </form>
        </div>
      </div>
    </>
  );
}

export default Login;
