import React, { useContext, useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { UserContext } from "../login/UserContext";
import Navbar from "../util/Navbar";

const UserManagement = () => {
  const [accounts, setAccounts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const { state } = useContext(UserContext); // Access the global state
  const navigate = useNavigate();

  const [editIndex, setEditIndex] = useState(-1);
  const [editValues, setEditValues] = useState({
    username: "",
    email: "",
    groupname: "",
    disabled: false, // Represented as a boolean
  });

  useEffect(() => {
    if (!state.isAuthenticated) {
      navigate("/");
      return;
    }

    const fetchData = async () => {
      try {
        const response = await axios.get(
          "http://localhost:8080/getAllAccounts",
          {
            withCredentials: true, // Ensure cookies are sent with the request
          }
        );
        setAccounts(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching accounts:", error);
        setError("Not Authorized to view page");
        setLoading(false);
      }
    };

    fetchData();
  }, [state.isAuthenticated, navigate]);

  const handleEdit = (index) => {
    const accountToEdit = accounts[index];
    setEditIndex(index);
    setEditValues({
      username: accountToEdit.username,
      email: accountToEdit.email,
      groupname: accountToEdit.groupname,
      disabled: accountToEdit.disabled, // Set disabled field as boolean
    });
  };

  const handleSave = async () => {
    try {
      const response = await axios.put(
        `http://localhost:8080/updateUser/${editValues.username}`,
        {
          email: editValues.email,
          groupname: editValues.groupname,
          disabled: editValues.disabled, // Send disabled as boolean
        }
      );
      const updatedAccounts = [...accounts];
      updatedAccounts[editIndex] = {
        ...updatedAccounts[editIndex],
        email: editValues.email,
        groupname: editValues.groupname,
        disabled: editValues.disabled, // Update disabled field
      };
      setAccounts(updatedAccounts);
      setEditIndex(-1); // Reset editIndex after successful update
    } catch (error) {
      console.error("Error updating user:", error);
      // Handle error state as needed
    }
  };

  const cancelEdit = () => {
    setEditIndex(-1); // Reset editIndex to cancel editing
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center">
      <Navbar />

      <div className="w-full max-w-6xl bg-white p-6 rounded-lg shadow-lg">
        <h1 className="text-2xl font-bold mb-4 text-center">User Management</h1>
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white">
            <thead className="bg-gray-200">
              <tr>
                <th className="py-2 px-4 border">Username</th>
                <th className="py-2 px-4 border">Email</th>
                <th className="py-2 px-4 border">Group</th>
                <th className="py-2 px-4 border">Status</th>
                <th className="py-2 px-4 border">Actions</th>
              </tr>
            </thead>
            <tbody>
              {accounts.map((account, index) => (
                <React.Fragment key={index}>
                  <tr>
                    <td className="py-2 px-4 border">
                      {editIndex === index ? (
                        <input
                          type="text"
                          value={editValues.username}
                          onChange={(e) =>
                            setEditValues({
                              ...editValues,
                              username: e.target.value,
                            })
                          }
                          disabled // Assuming username cannot be edited
                          className="p-2 border border-gray-300 rounded"
                        />
                      ) : (
                        account.username
                      )}
                    </td>
                    <td className="py-2 px-4 border">
                      {editIndex === index ? (
                        <input
                          type="text"
                          value={editValues.email}
                          onChange={(e) =>
                            setEditValues({
                              ...editValues,
                              email: e.target.value,
                            })
                          }
                          className="p-2 border border-gray-300 rounded"
                        />
                      ) : (
                        account.email
                      )}
                    </td>
                    <td className="py-2 px-4 border">
                      {editIndex === index ? (
                        <input
                          type="text"
                          value={editValues.groupname}
                          onChange={(e) =>
                            setEditValues({
                              ...editValues,
                              groupname: e.target.value,
                            })
                          }
                          className="p-2 border border-gray-300 rounded"
                        />
                      ) : (
                        account.groupname
                      )}
                    </td>
                    <td className="py-2 px-4 border">
                      {editIndex === index ? (
                        <select
                          value={editValues.disabled ? "Disabled" : "Enabled"}
                          onChange={(e) =>
                            setEditValues({
                              ...editValues,
                              disabled: e.target.value === "Disabled",
                            })
                          }
                          className="p-2 border border-gray-300 rounded"
                        >
                          <option value="Enabled">Enabled</option>
                          <option value="Disabled">Disabled</option>
                        </select>
                      ) : account.disabled ? (
                        "Disabled"
                      ) : (
                        "Enabled"
                      )}
                    </td>
                    <td className="py-2 px-4 border">
                      {editIndex === index ? (
                        <div>
                          <button
                            onClick={handleSave}
                            className="bg-green-500 text-white p-2 rounded mr-2"
                          >
                            Save
                          </button>
                          <button
                            onClick={cancelEdit}
                            className="bg-gray-500 text-white p-2 rounded"
                          >
                            Cancel
                          </button>
                        </div>
                      ) : (
                        <button
                          onClick={() => handleEdit(index)}
                          className="bg-blue-500 text-white p-1 rounded"
                        >
                          Edit
                        </button>
                      )}
                    </td>
                  </tr>
                </React.Fragment>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default UserManagement;
