import React, { useContext, useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { UserContext } from "../login/UserContext";
import Navbar from "../util/Navbar";

const UserManagement = () => {
  const [accounts, setAccounts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const { state } = useContext(UserContext); // Access the global state
  const navigate = useNavigate();

  const [editIndex, setEditIndex] = useState(-1);
  const [editValues, setEditValues] = useState({
    username: "",
    email: "",
    groupname: [],
    status: "Enabled", // Default to Enabled when creating a new user
  });

  useEffect(() => {
    if (!state.isAuthenticated) {
      navigate("/");
      return;
    }

    const fetchData = async () => {
      try {
        const response = await axios.get(
          "http://localhost:8080/getAllAccounts",
          {
            withCredentials: true, // Ensure cookies are sent with the request
          }
        );
        setAccounts(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching accounts:", error);
        setError("Not Authorized to view page");
        setLoading(false);
      }
    };

    fetchData();
  }, [state.isAuthenticated, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put(
        `http://localhost:8080/updateUser/${editValues.username}`,
        {
          email: editValues.email,
          groupname: editValues.groupname.join(","),
          disabled: editValues.status === "Disabled",
        }
      );
      const updatedAccounts = accounts.map((account, index) =>
        index === editIndex ? { ...account, ...editValues } : account
      );
      setAccounts(updatedAccounts);
      setEditIndex(-1); // Reset editIndex after successful update
    } catch (error) {
      console.error("Error updating user:", error);
      // Handle error state as needed
    }
  };

  const cancelEdit = () => {
    setEditIndex(-1); // Reset editIndex to cancel editing
  };

  const handleEdit = (index) => {
    const accountToEdit = accounts[index];
    setEditIndex(index);
    setEditValues({
      username: accountToEdit.username,
      email: accountToEdit.email,
      groupname: accountToEdit.groupname.split(","), // Assuming groupname is stored as a comma-separated string
      status: accountToEdit.disabled ? "Disabled" : "Enabled", // Adjust based on your data structure
    });
  };

  const handleCreateUser = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:8080/createUser", {
        username: editValues.username,
        email: editValues.email,
        groupname: editValues.groupname.join(","),
        disabled: editValues.status === "Disabled",
      });
      const newUser = response.data; // Assuming response.data contains the new user object
      setAccounts([...accounts, newUser]);
      setEditValues({
        username: "",
        email: "",
        groupname: [],
        status: "Enabled", // Reset form after successful creation
      });
    } catch (error) {
      console.error("Error creating user:", error);
      // Handle error state as needed
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center">
      <Navbar />

      <div className="w-full max-w-6xl bg-white p-6 rounded-lg shadow-lg">
        <h1 className="text-2xl font-bold mb-4 text-center">User Management</h1>
        <div className="mb-4">
          <label
            htmlFor="group-name"
            className="block text-gray-700 font-bold mb-2"
          >
            Enter Group Name:
          </label>
          <div className="flex">
            <input
              type="text"
              id="group-name"
              className="flex-grow p-2 border border-gray-300 rounded-l-lg"
            />
            <button className="bg-blue-500 text-white p-2 rounded-r-lg">
              + Create Group
            </button>
          </div>
        </div>
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white">
            <thead className="bg-gray-200">
              <tr>
                <th className="py-2 px-4 border">Username</th>
                <th className="py-2 px-4 border">Email</th>
                <th className="py-2 px-4 border">Password</th>
                <th className="py-2 px-4 border">Group</th>
                <th className="py-2 px-4 border">Status</th>
                <th className="py-2 px-4 border">Edit</th>
              </tr>
            </thead>
            <tbody>
              {accounts.map((account, index) => (
                <tr key={index}>
                  <td className="py-2 px-4 border">{account.username}</td>
                  <td className="py-2 px-4 border">{account.email}</td>
                  <td className="py-2 px-4 border">******</td>
                  <td className="py-2 px-4 border">
                    {account.groupname.split(",").map((group, index) => (
                      <span
                        key={index}
                        className="inline-block bg-gray-100 p-1 rounded mr-1"
                      >
                        {group}
                      </span>
                    ))}
                  </td>
                  <td className="py-2 px-4 border text-green-500">
                    {account.disabled ? "Disabled" : "Enabled"}
                  </td>
                  <td className="py-2 px-4 border">
                    <button
                      className="bg-blue-500 text-white p-1 rounded"
                      onClick={() => handleEdit(index)}
                    >
                      Edit
                    </button>
                  </td>
                </tr>
              ))}
              {editIndex !== -1 && (
                <tr>
                  <td colSpan="6" className="py-2 px-4 border">
                    <form onSubmit={handleSubmit}>
                      <input
                        type="text"
                        placeholder="Username"
                        className="p-2 border border-gray-300 rounded mr-2"
                        value={editValues.username}
                        disabled // Assuming username cannot be edited
                      />
                      <input
                        type="text"
                        placeholder="Email"
                        className="p-2 border border-gray-300 rounded mr-2"
                        value={editValues.email}
                        onChange={(e) =>
                          setEditValues({
                            ...editValues,
                            email: e.target.value,
                          })
                        }
                      />
                      <input
                        type="text"
                        placeholder="Group Name(s) (comma-separated)"
                        className="p-2 border border-gray-300 rounded mr-2"
                        value={editValues.groupname.join(", ")}
                        onChange={(e) =>
                          setEditValues({
                            ...editValues,
                            groupname: e.target.value.split(", "),
                          })
                        }
                      />
                      <select
                        className="p-2 border border-gray-300 rounded mr-2"
                        value={editValues.status}
                        onChange={(e) =>
                          setEditValues({
                            ...editValues,
                            status: e.target.value,
                          })
                        }
                      >
                        <option value="Enabled">Enabled</option>
                        <option value="Disabled">Disabled</option>
                      </select>
                      <button
                        type="submit"
                        className="bg-green-500 text-white p-2 rounded"
                      >
                        Save
                      </button>
                      <button
                        type="button"
                        className="bg-gray-500 text-white p-2 rounded ml-2"
                        onClick={cancelEdit}
                      >
                        Cancel
                      </button>
                    </form>
                  </td>
                </tr>
              )}
              <tr>
                <td className="py-2 px-4 border">
                  <input
                    type="text"
                    placeholder="Username"
                    className="p-2 border border-gray-300 rounded"
                  />
                </td>
                <td className="py-2 px-4 border">
                  <input
                    type="text"
                    placeholder="Email"
                    className="p-2 border border-gray-300 rounded"
                  />
                </td>
                <td className="py-2 px-4 border">
                  <input
                    type="password"
                    placeholder="Password"
                    className="p-2 border border-gray-300 rounded"
                  />
                </td>
                <td className="py-2 px-4 border">
                  <select className="p-2 border border-gray-300 rounded">
                    <option>DevTeam</option>
                    <option>Admins</option>
                    <option>Leads</option>
                  </select>
                </td>
                <td className="py-2 px-4 border">
                  <select className="p-2 border border-gray-300 rounded">
                    <option>Enabled</option>
                    <option>Disabled</option>
                  </select>
                </td>
                <td className="py-2 px-4 border">
                  <button
                    className="bg-green-500 text-white p-2 rounded"
                    onClick={handleCreateUser}
                  >
                    Create User
                  </button>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default UserManagement;
